#include <stdio.h>
#include "timing.h"
#include <unistd.h>
#include <fcntl.h>
#include <sys/types.h>
#include <sys/stat.h>

int main()
{
    unsigned long offset;
    int fp, num;
    struct timeval start,start0;
    char str[8192] = "";

    /**
     * @brief 顺序写
     */
    num = 0;
    offset = 0;
    fp = open("big.txt", O_RDWR + O_CREAT);
    if (fp == -1)
    {
        printf("00can not open the file\n");
        return 1;
    }
    start0 = time_start();
    while (num < 8)
    {
        start = time_start();
        // printf("offset:%ld\n", offset);
        // printf("sizeof(str):%ld\n",sizeof(str));
        lseek(fp, offset, SEEK_SET);
        // printf("%d\n",tell(fp));
        write(fp, str, sizeof(str));
        fsync(fp);
        // offset += sizeof(str);
        num++;
        printf("00-Comsumed Time:%ld μs\n", usdiff(start));
    }
    // flush(fp);
    close(fp);
    printf("0-Comsumed Time:%ld μs\n", usdiff(start0));

    /**
     * @brief 随机写
     */
    num = 0;
    char str2[8192] = "";
    offset = 0;
    fp = open("big.txt", O_RDWR + O_CREAT);
    start0 = time_start();
    while (num < 8) 
    {
        start = time_start();

        if (fp == -1)
        {
            printf("11can not open the file2\n");
            return 1;
        }
        // printf("offset:%ld\n", offset);
        // printf("sizeof(str):%ld\n",sizeof(str2));
        lseek(fp, offset, SEEK_SET);
        write(fp, str2, sizeof(str2));
        offset += sizeof(str) + 10240000;
        num++;
        fsync(fp);
        printf("11-Comsumed Time:%ld μs\n", usdiff(start));
    }
    close(fp);
    printf("1-Comsumed Time:%ld μs\n", usdiff(start0));

    return (0);
}